<script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>

<script>
const API = window.location.origin.includes("localhost") ? "http://localhost:8080/api/v6" : "/api/v6";
let token = sessionStorage.getItem('jwt_token');
let wfSteps = [];

function showToast(m) { $('#app-toast .toast-body').text(m); new bootstrap.Toast(document.getElementById('app-toast')).show(); }
function nav(v) { $('.view-section').hide(); $(`#view-${v}`).fadeIn(); if(v=='workflow') loadWorkflows(); if(v=='dashboard') loadStats(); }

async function req(url, opts={}) { 
    if(!opts.headers) opts.headers={}; opts.headers.Authorization=`Bearer ${token}`; 
    const res = await fetch(API+url, opts); 
    if(res.status==401) { $('#login-modal').fadeIn(); return null; } 
    return await res.json(); 
}

async function doLogin() { 
    const fd = new FormData(); fd.append('username',$('#l-user').val()); fd.append('password',$('#l-pass').val()); 
    const res = await fetch(API+'/token', {method:'POST',body:fd}); 
    if(res.ok) { token=(await res.json()).access_token; sessionStorage.setItem('jwt_token', token); $('#login-modal').fadeOut(); loadStats(); } 
}

// Workflow Logic
async function loadWorkflows() { const w = await req('/workflows'); $('#wf-list').html(w.map(i=>`<div class='card p-2 mb-2'>${i.name} <button class='btn btn-sm btn-info float-end' onclick="editWf('${i.id}')">Edit</button></div>`).join('')); }
async function editWf(id) { const w = await req(`/workflows/${id}`); wfSteps=w.steps; $('#wf-name').val(w.name); $('#wf-trigger').val(w.trigger); $('#wf-enabled').prop('checked', w.enabled!==false); nav('workflow-editor'); renderSteps(); }
function createNewWorkflow() { wfSteps = []; $('#wf-name').val(''); nav('workflow-editor'); renderSteps(); }

function addStep(parentArray) { 
    (parentArray || wfSteps).push({type:'email', config:{}}); 
    renderSteps(); 
}

function renderSteps() { 
    $('#wf-steps').html(renderStepRecursive(wfSteps)); 
}

function renderStepRecursive(steps, path=[]) {
    return steps.map((s, i) => {
        let html = `<div class='step-box'>
            <div class="d-flex justify-content-between">
                <select onchange='updateStepType(this, ${JSON.stringify([...path, i])})' class="form-select form-select-sm w-auto">
                    <option value='email' ${s.type=='email'?'selected':''}>Send Email</option>
                    <option value='condition' ${s.type=='condition'?'selected':''}>If Condition</option>
                    <option value='add_to_group' ${s.type=='add_to_group'?'selected':''}>Add to Group</option>
                </select>
                <button class="btn btn-sm btn-danger" onclick="deleteStep(${JSON.stringify([...path, i])})">x</button>
            </div>`;

        if(s.type == 'condition') {
            html += `<div class="mt-2 input-group input-group-sm">
                <input placeholder="Field" value="${s.field||''}" onchange="updateStepVal(this, ${JSON.stringify([...path, i])}, 'field')" class="form-control">
                <select class="form-select"><option>eq</option><option>neq</option></select>
                <input placeholder="Value" value="${s.value||''}" onchange="updateStepVal(this, ${JSON.stringify([...path, i])}, 'value')" class="form-control">
            </div>
            <div class="step-nested"><strong>Then:</strong> ${renderStepRecursive(s.then||[], [...path, i, 'then'])} <button class="btn btn-xs btn-light border" onclick="addNested(${JSON.stringify([...path, i, 'then'])})">+</button></div>
            <div class="step-nested"><strong>Else:</strong> ${renderStepRecursive(s.else||[], [...path, i, 'else'])} <button class="btn btn-xs btn-light border" onclick="addNested(${JSON.stringify([...path, i, 'else'])})">+</button></div>`;
        } else if (s.type == 'email') {
            html += `<input class="form-control form-control-sm mt-1" placeholder="To: {mail}" value="${s.to||''}" onchange="updateStepVal(this, ${JSON.stringify([...path, i])}, 'to')">`;
        }
        html += `</div>`;
        return html;
    }).join('');
}

// Helpers for deep update
window.updateStepType = (el, path) => { getStep(path).type = el.value; if(el.value=='condition'){getStep(path).then=[]; getStep(path).else=[];} renderSteps(); }
window.updateStepVal = (el, path, key) => { getStep(path)[key] = el.value; }
window.addNested = (path) => { getStep(path).push({type:'email'}); renderSteps(); }
window.deleteStep = (path) => { 
    let idx = path.pop(); 
    let arr = path.length ? getStep(path) : wfSteps;
    arr.splice(idx, 1);
    renderSteps(); 
}

function getStep(path) {
    let ref = wfSteps;
    for(let p of path) ref = ref[p];
    return ref;
}

async function saveWorkflow() { 
    const wf = {
        id: Date.now().toString(), 
        name: $('#wf-name').val(), 
        trigger: $('#wf-trigger').val(),
        enabled: $('#wf-enabled').is(':checked'),
        steps: wfSteps
    };
    await req('/workflows', {method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify(wf)}); 
    nav('workflow'); 
}

$(document).ready(() => { if(token) { $('#login-modal').hide(); } });
</script>
</body>
</html>
